import React, { useState } from 'react';
import './index'

const App = () => {
    const[fullname,setFullname]=useState({
        fname:"",
        lname:"",
    })
    
    const inputEvent=(event)=>{
        console.log(event.target.value)
        const value= event.target.value;
        const name= event.target.name;

        setFullname((preValue)=>{
            if(name==="fname"){
                return{
                    fname:value,
                    lName:preValue.lname,
                }
            }else if(name==="lname"){
                return{
                    fname:preValue.fname,
                    lname:value,
                } 
            }
        }
        )
    }
    
    const onSubmits=(event)=>{
        event.preventDefault()
        alert('Form Submitted')
    }
    return (
     <>
     <form onSubmit={onSubmits}>
     <div>
    <h1>Hello {fullname.fname} {fullname.lname} </h1>
        <input type='text' placeholder='Enter Your name' name ='fname' onChange={inputEvent} value={fullname.fname} />
        <input type='text' placeholder='Enter Your Password' name ='lname' onChange={inputEvent} value={fullname.lname} />
        <input type='email' placeholder='Enter Your email' name ='email' onChange={inputEvent} value={fullname.email} />
        <input type='number' placeholder='Enter Your Phone' name ='phone' onChange={inputEvent} value={fullname.phone} />
        
        <button type="submit">Click here</button>
     </div>
     </form>
    
    </>
    )

}

export default App;
