import React, { useState } from 'react';
import './index'

const App = () => {
    let time= new Date().toLocaleTimeString()
    const [ctime,setCtime]= useState(time)

    const getCtime=()=>{
        time=new Date().toLocaleDateString()
        setCtime(time)
    }
    return (
     <>
     <div className='time'>
        <h1>{ctime}</h1>
        <button onClick={getCtime}>click here</button>
    </div> 
    
    </>
    )
}

export default App;
